import { Text, View, StyleSheet, FlatList, TouchableHighlight } from 'react-native';

// or any pure javascript modules available in npm
import { Divider, TextInput } from 'react-native-paper';

export default function App() {

  const [title, setTitle] = useState('');
  const [titles, setTitles] = useState([
    {
      label: 'Marketing Coordinator',
      value: 'MC',
    },
    {
      label: 'Medical Assistant',
      value: 'MA',
    },
    {
      label: 'Web Designer',
      value: 'WD',
    },
    {
      label: 'Dog Trainer',
      value: 'DT',
    },
    {
      label: 'President of Sales',
      value: 'POS',
    },
    {
      label: 'Nursing Assistant',
      value: 'NA',
    },
    {
      label: 'Project Manager',
      value: 'PM',
    },
    {
      label: 'Librarian',
      value: 'L',
    },
  ]);
  const [titlesList, setTitlesList] = useState([]);

  const handleTitle = (text) => {
    let list = titles.filter((e) => e.label.toLowerCase().includes(text.toLowerCase()));
    setTitle(text);
    setTitlesList(list);
  }

  return (
    <View style={styles.container}>
      <TextInput
          value={title}
          label="Title*"
          onChangeText={(text) => handleTitle(text)}
          onFocus={() => setTitlesList(titles)}
        />
        <FlatList
          data={titlesList}
          style={{position: 'absolute', backgroundColor: '#FFF', zIndex: 1, top: '35%', width: '100%'}}
          renderItem={({ item }) => (
            <View style={{backgroundColor: '#FFF', width: '100%'}}>
              <TouchableHighlight
                style={styles.item}
                onPress={() => {
                  setTitle(item.label);
                  setTitlesList([]);
                }}>
                <Text style={styles.rowText}>
                  {item.label}
                </Text>
              </TouchableHighlight>
              <Divider style={{ backgroundColor: '#fafaFFA' }} />
            </View>
          )}
          keyExtractor={(item, index) => index.toString()}
        />
    </View>
  );
}

