export default class NewFeedScreen extends React.Component {


    constructor(props) {
        super(props);

        this.state = {
            text: "",               // to store text input
            textMessage: false,     // text input message flag
            loading: false,         // manage loader
            image: "",              // store image
            video: "",              // store video
        }
