                    </View>
                    <View style={styles.inputLayout}>
                        <Button icon="plus" mode="contained" onPress={() => this.createNewFeed()}>
                            Feed
                        </Button>
                    </View>
                </ScrollView>
                {/* loader */}
                {
                    this.state.loading && <Text>Loading</Text>
                }
            </View>
        )
    }

}

